.carouselContainer {
	position: relative;
	width: 100%;
	overflow: hidden;
	border-radius: 8px;
}

.imageContainer {
	width: 100%;
	height: 400px;
	position: relative;
}

.carouselImage {
	object-fit: cover;
}

.navButton {
	position: absolute;
	top: 50%;
	transform: translateY(-50%);
	background-color: rgba(0, 0, 0, 0.5);
	color: white;
	border: none;
	width: 40px;
	height: 40px;
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	cursor: pointer;
	font-size: 18px;
	z-index: 10;
	transition: background-color 0.3s;
}

.navButton:hover {
	background-color: rgba(0, 0, 0, 0.75);
}

.prevButton {
	left: 10px;
}

.nextButton {
	right: 10px;
}

.thumbnailContainer {
	display: flex;
	margin-top: 10px;
	gap: 8px;
	overflow-x: auto;
	padding-bottom: 5px;
}

.thumbnail {
	width: 64px;
	height: 64px;
	flex-shrink: 0;
	position: relative;
	cursor: pointer;
	border: 2px solid transparent;
	transition: border-color 0.3s;
}

.activeThumbnail {
	border-color: #0070f3;
}

.thumbnailImage {
	object-fit: cover;
}

.indicators {
	position: absolute;
	bottom: 15px;
	left: 50%;
	transform: translateX(-50%);
	display: flex;
	gap: 8px;
	z-index: 10;
}

.indicator {
	width: 8px;
	height: 8px;
	border-radius: 50%;
	background-color: rgba(255, 255, 255, 0.5);
	border: none;
	padding: 0;
	cursor: pointer;
}

.activeIndicator {
	background-color: #ffffff;
}

.placeholder {
	width: 100%;
	height: 300px;
	display: flex;
	align-items: center;
	justify-content: center;
	background-color: #f0f0f0;
	color: #666;
}

/* Responsive adjustments */
@media (max-width: 768px) {
	.imageContainer {
		height: 300px;
	}

	.thumbnail {
		width: 48px;
		height: 48px;
	}

	.navButton {
		width: 32px;
		height: 32px;
		font-size: 14px;
	}
}

@media (max-width: 480px) {
	.imageContainer {
		height: 250px;
	}
}
