.reviewsContainer {
	max-width: 1200px;
	margin: 0 auto;
	padding: 2rem 1rem;
}

.heading {
	text-align: center;
	margin-bottom: 2rem;
	font: bold 2rem "Cinzel", serif;
	color: #333;
}

.reviewGrid {
	display: grid;
	grid-template-columns: repeat(3, 1fr);
	gap: 1.5rem;
}

.reviewCard {
	background-color: #f9f9f9;
	border-radius: 10px;
	padding: 1.5rem;
	text-align: center;
	box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
	transition: transform 0.3s ease;
}

.reviewCard:hover {
	transform: scale(1.03);
}

.starContainer {
	display: flex;
	justify-content: center;
	margin-bottom: 1rem;
}

.star {
	color: #ddd;
	font-size: 1.5rem;
	margin: 0 0.25rem;
}

.star.filled {
	color: #ffd700;
}

.testimony {
	font-style: italic;
	font-family: "Nunito", serif;
	color: #666;
	margin-bottom: 1rem;
}

.name {
	font-weight: bold;
	color: #333;
}

/* Responsive Design */
@media screen and (max-width: 1024px) {
	.reviewGrid {
		grid-template-columns: repeat(2, 1fr);
	}
}

@media screen and (max-width: 768px) {
	.reviewGrid {
		grid-template-columns: 1fr;
	}

	.heading {
		font-size: 1.5rem;
	}
}
